let techalgospotlightHeroSlider = function (e) {
  let o = e.querySelector(".techalgospotlight-spinner"),
    t = function () {
      o.classList.remove("visible"),
        setTimeout(function () {
          o.style.display = "none";
        }, 300),
        e.classList.add("loaded");
    };
  return (
    imagesLoaded(e, function () {
      document.getElementById("techalgospotlight-preloader") &&
      !document.body.classList.contains("techalgospotlight-loaded")
        ? document.body.addEventListener(
            "techalgospotlight-preloader-done",
            function () {
              setTimeout(t, 300);
            }
          )
        : setTimeout(t, 300);
    }),
    e
  );
};
document.addEventListener("DOMContentLoaded", function () {
  document.querySelectorAll(".techalgospotlight-hero-slider").forEach((e) => {
    techalgospotlightHeroSlider(e);
  });
});
