!(function ($) {
  "use strict";
  wp.customize.controlConstructor["techalgospotlight-sortable"] =
    wp.customize.Control.extend({
      ready: function () {
        var control = this;
        control.params.sortable &&
          control.container.find("ul").sortable({
            items: "> .techalgospotlight-sortable-item",
            intersect: "pointer",
            axis: "y",
            update: function () {
              control.update();
            },
          }),
          control.container.on("click", ".dashicons-visibility", function () {
            $(this)
              .closest(".techalgospotlight-sortable-item")
              .toggleClass("invisible"),
              control.update();
          });
      },
      update: function () {
        var $item,
          items = this.container.find(".techalgospotlight-sortable-item"),
          new_val = {};
        _.isEmpty(items) ||
          _.each(items, function (item) {
            ($item = $(item)),
              (new_val[$item.data("value")] = !$item.hasClass("invisible"));
          }),
          this.setting.set({}),
          this.setting.set(new_val);
      },
    });
})(jQuery);
