/**
 * Filters the arguments for a single nav menu item to include dropdown indicators.
 *
 * @since 1.0.0
 *
 * @param stdClass $args  An object of wp_nav_menu() arguments.
 * @param WP_Post  $item  Menu item data object.
 * @param int      $depth Depth of menu item. Used for padding.
 */
function techalgospotlight_nav_menu_item_args($args, $item, $depth)
{

	if (techalgospotlight_option('main_nav_sub_indicators')) {
		$dropdown_indicator = techalgospotlight()->icons->get_svg('chevron-down');
		if (in_array('menu-item-has-children', $item->classes, true)) {
			if (false === strpos($args->link_after, $dropdown_indicator)) {
				$args->link_after .= $dropdown_indicator;
				$args->after = '<button type="button" class="techalgospotlight-mobile-toggen">' . $dropdown_indicator . '</button>';
			}
		} else {
			$args->link_after = str_replace($dropdown_indicator, '', $args->link_after);
			$args->after = '';
		}
	}

	return $args;
}
add_filter('nav_menu_item_args', 'techalgospotlight_nav_menu_item_args', 10, 3);


/**
 * Display social icons in social links menu.
 *
 * @param  string  $item_output The menu item output.
 * @param  WP_Post $item        Menu item object.
 * @param  int     $depth       Depth of the menu.
 * @param  array   $args        wp_nav_menu() arguments.
 * @return string  $item_output The menu item output with social icon.
 */
function techalgospotlight_nav_menu_social_icons($item_output, $item, $depth, $args)
{

	// Get supported social icons.
	$social_icons = techalgospotlight_social_links_icons();

	// Change SVG icon inside social links menu if there is supported URL.
	if (false !== strpos($args->menu_class, 'techalgospotlight-socials-menu')) {

		foreach ($social_icons as $attr => $value) {
			if (false !== strpos($item_output, $attr)) {
				$item_output = str_replace(
					$args->link_after,
					'</span><span class="' . esc_attr($value) . '" title="' . esc_attr(ucfirst($value)) . '">' . techalgospotlight()->icons->get_svg($value, array('aria-hidden' => 'true')) . techalgospotlight()->icons->get_svg(
						$value,
						array(
							'class' => 'bottom-icon',
							'aria-hidden' => 'true',
						)
					),
					$item_output
				);
			}
		}
	}

	return $item_output;
}
add_filter('walker_nav_menu_start_el', 'techalgospotlight_nav_menu_social_icons', 10, 4);

