	/**
	 * techalgospotlight Icons class.
	 *
	 * @since 1.0.0
	 */
	class techalgospotlight_Icons
	{
		/**
		 * Primary class constructor.
		 *
		 * @since 1.0.0
		 */
		public function __construct()
		{
		}

		/**
		 * GET SVG CODE
		 * Get the SVG code for the specified icon
		 *
		 * @param string $icon Icon name.
		 * @param array  $args Parameters needed to display an SVG.
		 */
		public function get_svg($icon = '', $args = array())
		{

			$arr = self::get();

			if (!array_key_exists($icon, $arr)) {
				return null;
			}

			$args = wp_parse_args(
				$args,
				array(
					'class' => '',
				)
			);

			// .techalgospotlight-icon is a required class.
			if (false === strpos($args['class'], 'techalgospotlight-icon')) {
				$args['class'] = trim('techalgospotlight-icon ' . $args['class']);
			}

			$repl = '<svg ';

			if (!empty($args)) {
				foreach ($args as $key => $value) {
					if (!empty($value)) {
						$repl .= esc_html($key) . '="' . esc_attr($value) . '" ';
					}
				}
			}

			$svg = preg_replace('/^<svg /', $repl, trim($arr[$icon])); // Add extra attributes to SVG code.
			$svg = preg_replace("/([\n\t]+)/", ' ', $svg); // Remove newlines & tabs.
			$svg = preg_replace('/>\s*</', '><', $svg);    // Remove whitespace between SVG tags.

			// Make sure that only our allowed tags and attributes are included.
			$svg = wp_kses(
				$svg,
				array(
					'svg' => array(
						'class' => true,
						'xmlns' => true,
						'width' => true,
						'height' => true,
						'viewbox' => true,
						'aria-hidden' => true,
						'aria-label' => true,
						'role' => true,
						'focusable' => true,
						'fill' => true,
					),
					'path' => array(
						'fill' => true,
						'fill-rule' => true,
						'd' => true,
						'transform' => true,
						'stroke' => true,
						'stroke-linecap' => true,
						'stroke-linejoin' => true,
						'stroke-miterlimit' => true,
						'stroke-width' => true,
					),
					'polygon' => array(
						'fill' => true,
						'fill-rule' => true,
						'points' => true,
						'transform' => true,
						'focusable' => true,
					),
				)
			);

			if (!$svg) {
				return null;
			}

			return $svg;
		}

		/**
		 * Get SVG icons array.
		 *
		 * @return array
		 */
		public function get()
		{
			return apply_filters('techalgospotlight_icons_svg', self::$icons);
		}

		/**
		 * Get all available SVG icons
		 *
		 * @return array
		 */
		public function get_all_svg_icons()
		{

			$arr = self::get();

			$return = array();

			foreach ($arr as $icon => $svg) {

				$svg = self::get_svg($icon);

				if ($svg) {
					$return[$icon] = self::get_svg($icon);
				}
			}

			return $return;
		}

		/**
		 * Get icon for post entry meta.
		 *
		 * @since  1.0.0
		 * @param  string $slug    Icon slug.
		 * @param  string $icon    Icon HTML markup.
		 * @param  string $post_id Current post ID.
		 * @return string          Icon HTML markup.
		 */
		public function get_meta_icon($slug = '', $icon = '', $post_id = '')
		{

			$return = '';

			if (is_single($post_id)) {
				if (techalgospotlight_option('single_entry_meta_icons')) {
					$return = $icon;
				}
			} elseif (!is_single()) {
				if (techalgospotlight_option('entry_meta_icons')) {
					$return = $icon;
				}
			}

			return apply_filters('techalgospotlight_' . $slug . '_meta_icon', $return, $post_id);
		}

